import React from 'react';
import { Pie } from 'react-chartjs-2';
import './app.css';

const data = {
  labels: ['A', 'B', 'C'],
  datasets: [
    {
      data: [20, 30, 50],
      backgroundColor: [
        'rgba(255, 99, 132, 0.6)',
        'rgba(54, 162, 235, 0.6)',
        'rgba(255, 206, 86, 0.6)',
      ],
    },
  ],
};

const options = {
  responsive: true,
};

const PieChart = () => (
  <div className="chart-container">
    <h1>Gr√°fico de Pizza</h1>
    <Pie data={data} options={options} />
  </div>
);

export default PieChart;
