import React, { useState } from 'react';

function App() {
  const [cdbValues, setCdbValues] = useState({});

  const handleInputChange = (event, cdbName) => {
    const { value } = event.target;
    setCdbValues(prevState => ({
      ...prevState,
      [cdbName]: value
    }));
  };

  return (
    <div>
      <label>
        <input
          type="checkbox"
          onChange={(event) => handleInputChange(event, 'Cdb1')}
        />
        Cdb1
      </label>
      <input
        type="text"
        disabled={!cdbValues['Cdb1']}
        onChange={(event) => handleInputChange(event, 'Cdb1Value')}
        placeholder="Valor para Cdb1"
      />
      <br />
      <label>
        <input
          type="checkbox"
          onChange={(event) => handleInputChange(event, 'Cdb2')}
        />
        Cdb2
      </label>
      <input
        type="text"
        disabled={!cdbValues['Cdb2']}
        onChange={(event) => handleInputChange(event, 'Cdb2Value')}
        placeholder="Valor para Cdb2"
      />
      <br />
      <label>
        <input
          type="checkbox"
          onChange={(event) => handleInputChange(event, 'Cdb3')}
        />
        Cdb3
      </label>
      <input
        type="text"
        disabled={!cdbValues['Cdb3']}
        onChange={(event) => handleInputChange(event, 'Cdb3Value')}
        placeholder="Valor para Cdb3"
      />
      <br />
      <button onClick={() => console.log(cdbValues)}>Salvar</button>
    </div>
  );
}

export default App;
