import React, { useState } from 'react';

function CdbInput() {
  const [investimentos, setInvestimentos] = useState({
    cdb1: 0,
    cdb2: 0,
    cdb3: 0
  });

  const handleCheckboxChange = (event, cdbName) => {
    const isChecked = event.target.checked;
    setInvestimentos(prevState => ({
      ...prevState,
      [cdbName]: isChecked ? 0 : prevState[cdbName]
    }));
  };

  const handleInputChange = (event, cdbName) => {
    const { value } = event.target;
    setInvestimentos(prevState => ({
      ...prevState,
      [cdbName]: parseFloat(value)
    }));
  };

  return (
    <div>
      <label>
        <input
          type="checkbox"
          onChange={(event) => handleCheckboxChange(event, 'cdb1')}
        />
        Cdb1
      </label>
      <input
        type="text"
        disabled={!investimentos['cdb1']}
        onChange={(event) => handleInputChange(event, 'cdb1')}
        placeholder="Valor para Cdb1"
      />
      <br />
      <label>
        <input
          type="checkbox"
          onChange={(event) => handleCheckboxChange(event, 'cdb2')}
        />
        Cdb2
      </label>
      <input
        type="text"
        disabled={!investimentos['cdb2']}
        onChange={(event) => handleInputChange(event, 'cdb2')}
        placeholder="Valor para Cdb2"
      />
      <br />
      <label>
        <input
          type="checkbox"
          onChange={(event) => handleCheckboxChange(event, 'cdb3')}
        />
        Cdb3
      </label>
      <input
        type="text"
        disabled={!investimentos['cdb3']}
        onChange={(event) => handleInputChange(event, 'cdb3')}
        placeholder="Valor para Cdb3"
      />
      <br />
      <button onClick={() => console.log(investimentos)}>Salvar</button>
    </div>
  );
}

export default CdbInput;
