import React, { useRef, useEffect } from 'react';
import { Pie } from 'react-chartjs-2'; // Verifique se está importando o Pie corretamente
import './app.css'; // Importe seu arquivo CSS aqui

const PieChart = () => {
  const chartRef = useRef(null);
  const data = {
    labels: ['A', 'B', 'C'],
    datasets: [
      {
        data: [20, 30, 50], // Porcentagens
        backgroundColor: [
          'rgba(255, 99, 132, 0.6)',
          'rgba(54, 162, 235, 0.6)',
          'rgba(255, 206, 86, 0.6)',
        ],
      },
    ],
  };

  const options = {
    responsive: true,
  };

  useEffect(() => {
    // Destrói o gráfico anterior quando o componente é montado ou atualizado
    if (chartRef && chartRef.current) {
      chartRef.current.chartInstance.destroy();
    }
  }, []);

  return (
    <div className="chart-container">
      <h1>Gráfico de Pizza</h1>
      <Pie ref={chartRef} data={data} options={options} />
    </div>
  );
};

export default PieChart;
