import React, { useState } from 'react';
import Grafico from "./App copy"

function CdbInput() {
  const [investimentos, setInvestimentos] = useState({
    cdb1: 0,
    cdb2: 0,
    cdb3: 0
  });

  const handleInputChange = (event, cdbName) => {
    const { value } = event.target;
    setInvestimentos(prevState => ({
      ...prevState,
      [cdbName]: value
    }));
  };

  var cdb01 = document.getElementById("cdb1")

  const AlterarInput = (check, input) =>{
    check.checked == true ? input.disabled = false : input.checked = true
  }

  return (
    <main id='Investir'>

      <section id='content'>

        <div>

          <label onClick={event => AlterarInput(document.getElementById)}>
            <input
              type="checkbox"
              onChange={(event) => handleInputChange(event, 'cdb1')}
              id='cdb1'
              className='papeis'
            />
            Cdb1
          <input
            type="number"
            onChange={(event) => handleInputChange(event, 'cdb1')}
            placeholder="R$ 00.00"
            id='valorcdb1'
            className='valores'
          />
          </label>

        </div>

        <br />

        <div>

          <label>

            <input
              type="checkbox"
              onChange={(event) => handleInputChange(event, 'cdb2')}
              id='cdb2'
              className='papeis'
            />
            Cdb2

            <input
              type="number"
              onChange={(event) => handleInputChange(event, 'cdb2')}
              placeholder="R$ 00.00"
              className='valores'
            />

          </label>


        </div>

        <br />

        <div>

          <label>
            <input
              type="checkbox"
              onChange={(event) => handleInputChange(event, 'cdb3')}
              id='cdb3'
              className='papeis'
            />
            Cdb3
            <input
              type="number"
              onChange={(event) => handleInputChange(event, 'cdb3')}
              placeholder="R$ 00.00"
              className='valores'
            />
          </label>


        </div>

        <br />
        <button button onClick={() => console.log(investimentos)}>Salvar</button>
      </section>

      <section id='grafico'>
        <Grafico/>
      </section>

    </main>
  );
}

export default CdbInput;
