import React, { useState } from 'react';
import Grafico from "./App copy"

function CdbInput() {
  const [investimentos, setInvestimentos] = useState({
    cdb1: 0,
    cdb2: 0,
    cdb3: 0
  });

  const handleInputChange = (event, cdbName) => {
    const { value } = event.target;
    setInvestimentos(prevState => ({
      ...prevState,
      [cdbName]: parseFloat(value)
    }));
  };

  return (
    <main>

      <section>

        <div>
          <label>
            <input
              type="checkbox"
              onChange={(event) => handleInputChange(event, 'cdb1')}
              id='cdb1'
            />
            Cdb1
          </label>
          <input
            type="text"
            onChange={(event) => handleInputChange(event, 'cdb1')}
            placeholder="R$ 00.00"
          />

        </div>

        <br />

        <div>

          <label>
            <input
              type="checkbox"
              onChange={(event) => handleInputChange(event, 'cdb2')}
              id='cdb2'
            />
            Cdb2
          </label>

          <input
            type="text"
            onChange={(event) => handleInputChange(event, 'cdb2')}
            placeholder="R$ 00.00"
          />

        </div>

        <br />

        <div>

          <label>
            <input
              type="checkbox"
              onChange={(event) => handleInputChange(event, 'cdb3')}
              id='cdb3'
            />
            Cdb3
          </label>

          <input
            type="text"
            onChange={(event) => handleInputChange(event, 'cdb3')}
            placeholder="R$ 00.00"
          />

        </div>
      </section>

      <section id='grafico'>
        <Grafico></Grafico>
      </section>


      <br />
      <button onClick={() => console.log(investimentos)}>Salvar</button>
    </main>
  );
}

export default CdbInput;
