import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [price, setPrice] = useState(null);

  useEffect(() => {
    const interval = setInterval(() => {
      axios.get('https://bvmf.bmfbovespa.com.br/pt-br/mercados/acoes/empresas/ExecutaAcaoConsultaInfoEmp.asp?CodCVM=19348&ViewDoc=1&AnoDoc=2019&VersaoDoc=1&NumSeqDoc=80051')
        .then(response => {
          setPrice(response.data.price);
        })
        .catch(error => {
          console.error(error);
        });
    }, 60000); // Atualiza a cada minuto (60000 milissegundos)

    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      <h1>Preço da ação ITUB4</h1>
      <p>**Atualizado em: {new Date().toLocaleString()}**</p>
      <h2>R$ {price}</h2>
    </div>
  );
};

export default App;
